import { Component } from "react";
import onepiece from './onepiece.jpg'
import './index.css';

class MovieCard extends Component {

    addStars = () => {

        if (this.state.stars >= 5) {
            return;
        }

        this.setState({
            stars: this.state.stars + 1
        })
    }

    subStars = () => {

        if (this.state.stars <= 0) {
            return;
        }

        this.setState((prev) => {
            return {
                stars: prev.stars - 1
            }
        })
    }

    handleFav = () => {
        this.setState({
            fav: !this.state.fav
        });
    }

    handleBuy = () => {
        this.setState({
            buy: !this.state.buy
        });
    }

    render() {
        const { title, plot, price, rating, stars, fav, buy} = this.props.moviedata;
        return (
            <div className="movies">
                <div className="moviecard">
                    <section className="left">
                        <img alt="Poster" src={onepiec} />
                    </section>
                    <section className="right">
                        <div className="title">{title}</div>
                        <div className="plot">{plot}</div>
                        <div className="price">â‚¹{price}</div>

                        <div className="foot">
                            <div className="rating">{rating}</div>
                            <div className="stars">
                                <img className="minus" alt="min" src="https://cdn-icons-png.flaticon.com/128/43/43625.png"
                                    onClick={this.subStars} />
                                <span className="starCount">{stars}</span>
                                <img className="star" alt="s" src="https://cdn-icons-png.flaticon.com/128/1828/1828884.png" />
                                <img className="plus" alt="pls" src="https://cdn-icons-png.flaticon.com/128/1828/1828925.png"
                                    onClick={this.addStars} />
                            </div>


                            <button className={fav ? "fav" : "unfav"}
                                onClick={this.handleFav}>Favourite</button>

                            <button className={buy ? "buy" : "remove"}
                                onClick={this.handleBuy}>{buy ? "Add Cart" : "Remove"}</button>
                        </div>
                    </section>
                </div>
            </div>
        )

    }
}

export default MovieCard;
